public int numJewelsInStones(String J, String S) {
Set<String> jewelsSet = new HashSet<>(J.length());
char[] jewelsArray = J.toCharArray();
for (int i = 0; i < jewelsArray.length; i++) {
jewelsSet.add(String.valueOf(jewelsArray[i]));
}
int count = 0;
