TreeNode l4 = new TreeNode(4);
l4.left = new TreeNode(7);

TreeNode l2 = new TreeNode(2);
l2.left = l4;
l2.right = new TreeNode(5);

TreeNode r3 = new TreeNode(3);
TreeNode r6 = new TreeNode(6);
TreeNode r8 = new TreeNode(8);
