public int subtractProductAndSum(int n) {
int sum = 0;
int product = 1;
while (true) {
if (n / 10 != 0) {
int y = n % 10;
sum = sum + y;
product = product * y;
n = (n - y) / 10;
} else {
break;
}
}
return product * n - (sum + n);
}
